const css="<style>.czw{font-family:-apple-system,BlinkMacSystemFont,Segoe UI,Roboto,Oxygen-Sans,Ubuntu,Cantarell,Helvetica Neue,sans-serif;display:flex;flex-direction:row;justify-content:space-around;height:100%;width:100%}.czw__title__text{width:25%}.czw__graph{width:50%;padding:10px}.czw__stats{width:25%;display:flex;flex-direction:column}.czw__stats__single--avg{background-color:red;flex:1}.czw__stats__single--increase{background-color:orange;flex:1}</style>",url="http://localhost:8000/build/data.json",widget=document.getElementById("czw");fetch(url).then((function(t){if(!t.ok)throw Error(t);return t.json()})).then((function(t){widget.insertAdjacentHTML("beforeEnd",css),widget.insertAdjacentHTML("beforeEnd",`<div id="czw" class="czw"> \t\t<div class="czw__title"> \t\t\t<span class="czw__title__text">CO2</span>       </div> \t\t<div class="czw__graph"> \t\t\t${decodeURIComponent(t.chart)}\n \t\t</div> \t\t<div class="czw__stats"> \t\t\t<div class="czw__stats__single czw__stats__single--avg"> \t\t\t\t<p class="czw_stats__label">7-day average</p> \t\t\t\t<p class="czw_stats__value">${t.average}</p> \t\t\t</div> \t\t\t<div class="czw__stats__single czw__stats__single--increase"> \t\t\t\t<p class="czw_stats__label">In last 2 years</p> \t\t\t\t<p class="czw_stats__value">${t.change}</p> \t\t\t</div> \t\t</div> \t</div>`)})).catch((function(t){widget.innerHTML="No Result",console.log(t)}));