const css="<style>.czw{font-family:-apple-system,BlinkMacSystemFont,Segoe UI,Roboto,Oxygen-Sans,Ubuntu,Cantarell,Helvetica Neue,sans-serif;display:flex;flex-direction:column;align-items:center;width:100%;max-width:600px}@media (min-width:740px){.czw{flex-direction:row;align-items:stretch;justify-content:space-around;margin:0 auto}}.czw__title__text{width:25%}.czw__graph{width:80%}@media (min-width:320px){.czw__graph{width:70%}}@media (min-width:740px){.czw__graph{width:50%;padding:10px}}.czw__stats{width:80%;display:flex;flex-direction:column}@media (min-width:320px){.czw__stats{width:70%;flex-direction:row}}@media (min-width:740px){.czw__stats{width:25%;flex-direction:column}}.czw__stats__single--avg{background-color:#fc5a03;flex:1;text-align:left;color:#fff;padding-left:5px}.czw__stats__single--increase{background-color:orange;flex:1;text-align:left;color:#000;padding-left:5px}</style>",url="http://localhost:8000/build/data.json",widget=document.getElementById("czw");fetch(url).then((function(t){if(!t.ok)throw Error(t);return t.json()})).then((function(t){widget.insertAdjacentHTML("beforeEnd",css),widget.insertAdjacentHTML("beforeEnd",`<div id="czw" class="czw"> \t\t<div class="czw__title"> \t\t\t<span class="czw__title__text">CO2</span>       </div> \t\t<div class="czw__graph"> \t\t\t${decodeURIComponent(t.chart)}\n \t\t</div> \t\t<div class="czw__stats"> \t\t\t<div class="czw__stats__single czw__stats__single--avg"> \t\t\t\t<p class="czw_stats__label">7-day average</p> \t\t\t\t<p class="czw_stats__value">${t.average}</p> \t\t\t</div> \t\t\t<div class="czw__stats__single czw__stats__single--increase"> \t\t\t\t<p class="czw_stats__label">In last 2 years</p> \t\t\t\t<p class="czw_stats__value">${t.change}</p> \t\t\t</div> \t\t</div>\t </div>`)})).catch((function(t){widget.innerHTML="No Result",console.log(t)}));